<template><div class='auth-card card'><h2>Login</h2><p>Demo: user@example.com / 12345</p><form @submit.prevent='login'><input class='form-input' v-model='email' placeholder='Email' required/><input class='form-input' type='password' v-model='password' placeholder='Password' required/><div style='display:flex;gap:8px'><button class='btn btn-primary' type='submit'>Login</button></div></form><p v-if='error' style='color:red'>{{error}}</p></div></template><script>export default{data(){return{email:'',password:'',error:''}},methods:{login(){const demo={email:'user@example.com',password:'12345'}; if(this.email===demo.email && this.password===demo.password){ localStorage.setItem('ticketapp_session','demo-token'); this.$router.push('/dashboard') } else { this.error='Invalid credentials' } }}}</script>